SELECT p.post_id, p.title, p.author, TO_CHAR(p.created_at, 'YYYY-MM-DD') AS created_at_formatted
FROM post p
JOIN user_activity ua ON p.post_id = ua.post_id
WHERE ua.user_id = (
    SELECT u.user_id
    FROM `user` u
    WHERE u.username = #{username}
) AND ua.activity_type = #{activity_type};

SELECT COUNT(*)
FROM post p
JOIN user_activity ua ON p.post_id = ua.post_id
WHERE ua.user_id = (
    SELECT u.user_id
    FROM `user` u
    WHERE u.username = #{username}
) AND ua.activity_type = 'like'
AND ua.post_id = #{post_id};
